import React from "react";
import { Link } from "react-router-dom";
import HeroImage from "../images/hero.svg";
import {
  HeroSectionContainer,
  HeroSectionContainerReverse,
  LeftSide,
  LeftSideReverse,
  RightSide,
} from "./styling/HeroSection.styled";
import { Button } from "./Button";

export const HeroSection = ({ title, message, buttonDisplay, reverse }) => {
  return !reverse ? (
    <>
      <HeroSectionContainer>
        <LeftSide>
          <h1>{title}</h1>
          <p>{message}</p>
          {buttonDisplay && (
            <Link to="/about">
              <Button value="Learn More" styleClass="btn-large" />
            </Link>
          )}
        </LeftSide>
        <RightSide>
          <img src={HeroImage} alt="Hero Image" />
        </RightSide>
      </HeroSectionContainer>
    </>
  ) : (
    <>
      <HeroSectionContainerReverse>
        <RightSide>
          <img src={HeroImage} alt="Hero Image" />
        </RightSide>
        <LeftSideReverse>
          <h1>{title}</h1>
          <p>{message}</p>
          {buttonDisplay && (
            <Link to="/about">
              <Button value="Learn More" styleClass="btn-large" />
            </Link>
          )}
        </LeftSideReverse>
      </HeroSectionContainerReverse>
    </>
  );
};
