import styled from "styled-components";

export const ServicesContainer = styled.div``;

export const ServicesText = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 50px;

  & > h1 {
    font-size: 42px;
    font-weight: 800;
  }

  & > p {
    color: #767676;
    max-width: 597px;
    text-align: center;
  }
`;

export const ServicesChange = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;

  & > p {
    font-weight: bold;
  }
`;

export const Changer = styled.div`
  width: 40px;
  height: 24px;
  background: #ccd5ae;
  border-radius: 50px;
  margin: 50px 100px;
  position: relative;
  cursor: pointer;

  &:after {
    content: "";
    position: absolute;
    width: 22px;
    height: 22px;
    background: #666b55;
    border-radius: 50%;
    left: 42%;
    top: 5%;
  }
`;

export const ServicesCardContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  padding: 0 100px;
`;

export const ServicesCard = styled.div`
  width: 397px;
  height: 397px;
  border-radius: 20px;
  box-shadow: rgba(50, 50, 93, 0.25) 0px 30px 60px -12px,
    rgba(0, 0, 0, 0.3) 0px 18px 36px -18px;
  margin-top: 15px;
`;
